label("$48^\circ$",(3,1.5),W);
label("$60^\circ$",(-1.2,-2*sqrt(3)),N);
label("$x^\circ$",(1,-1));
label("$A$",(3,5.5));
label("$B$",(-2,-2*sqrt(3)),S);
label("$C$",(3,-2*sqrt(3)),SE);
label("$D$",(3,2.7),E);
label("$E$",(0,0),W);
label("$F$",(-2*sqrt(3)/.9,-2*sqrt(3)),SW);
[/asy]

